import React, { Component, useState } from 'react';
import Cookies from 'universal-cookie'
import { 
  Header,
  Skills,
  Projects,
  Location,
  Contact,
  Footer,
  Nav
} from '../../Components';
import '../../App.css';
import './style.css'

const Home = (props) => {
  const [pageLocation, setPageLocation] = useState('home')

  return (
    <div id='home-container'>
      <div className='home-location'>
        <div>{`${pageLocation} // samuelwright.dev`}</div>
      </div>

      <Nav pageLocation={pageLocation} setPageLocation={setPageLocation}/>

      {pageLocation === 'home' && 
        <div className='intro-section'>
          <p>Hi, I'm Samuel Wright, a <strong>full-stack developer</strong> focused on creating <strong>beautiful</strong> and <strong>user friendly</strong> applications while writing <strong>clean code</strong>.</p>
        </div>}
      {pageLocation === 'projects' && 
        <div className='section projects-section' id='projects'>
          <Projects />
        </div>}
      {pageLocation === 'skills' && 
        <div className='section skills-section' id='skills'>
          <Skills />
        </div>}
      {pageLocation === 'work' && 
        <div className='section work-section' id='work'>
          <h3>Work</h3>
        </div>}
      {pageLocation === 'contact' && 
        <div className='section contact-section' id='contact'>
          <Contact />
        </div>}
    </div>
  )
}

export default Home;
