import React, { useContext, useEffect, useRef, useState } from 'react';
import { DataContext } from '../../Context/DataContext';
import { ThemeContext } from '../../Context/ThemeContext';

import { db } from '../../firebase';
import './style.css'

const Contact = (props) => {
    const [formData, setFormData] = useState({
        name: '',
        email: '',
        message: '',
    })
    const [dataSent, setDataSent] = useState(false)

    const { setPageLocation } = useContext(DataContext)
    const { theme } = useContext(ThemeContext)

    const hiddenRef = useRef();

    useEffect(() => {
        window.addEventListener('scroll', scrollHandler);
        return () => window.removeEventListener('scroll', scrollHandler);
    }, []);

    const scrollHandler = () => {
        if(window.pageYOffset + window.innerHeight >= hiddenRef.current.offsetTop + 125) {
            setPageLocation('contact')
        }
    }

    function makeid(length) {
        var result = "";
        var characters =
          "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789";
        var charactersLength = 10;
        for (var i = 0; i < length; i++) {
          result += characters.charAt(Math.floor(Math.random() * charactersLength));
        }
        return result;
    }

    const handleChange = e => {
        setFormData(prevData => {
            return {
                ...prevData,
                [e.target.name]: e.target.value
            }
        })
    }

    function handleSubmit(event) {
        event.preventDafault()

        // Make a unique name for the message
        var d = new Date();
        var msgName = String(formData.name + '-' + d.getTime());

        // Get date of message
        var date = String( d.getMonth() + 1) + '-' + String( d.getDate()) + '-' + String(d.getFullYear())

        // Get time of message
        var minutes = ()=>{
            var a;
            if (d.getMinutes() < 10) {
                a = String('0' + d.getUTCMinutes());
            } else {
                a = String(d.getUTCMinutes());
            }

            return a;
        }

        var time = String(d.getHours()) + ':' + minutes() + '.' + String(d.getSeconds());

        // Make new id
        var id = makeid(10);

        // Add message to database
        db.collection("messages").doc(msgName).set({
            name: formData.name,
            email: formData.email,
            message: formData.message,
            date: date,
            time: time,
            timezone: d.getTimezoneOffset(),
            id: id
        })
        .then(() => {
            console.log("Great news! Message sent successfully!");
            /*console.log({
                name: formData.name,
                email: formData.email,
                message: formData.message,
                date: date,
                time: time,
                timezone: d.getTimezoneOffset(),
                id: id
            })*/
        })
        .catch((error) => {
            console.error("Krikey! Error sending message: ", error);
        });

        // For component to show message was sent
        if (!dataSent){
            setDataSent(true)
        } 
    }

    function resetSend() {
        setDataSent(false)
    }


    let contactInfoStyle = {
        textAlign: 'center',
        marginTop: 15,
        width: '100%',
    }
    let contactInfoStyle2 = {
        textAlign: 'center',
        fontSize: '.8em',
        color: 'rgb(1,1,1,.25)',
    }
    let formOptionStyle = {
        textAlign: 'center'
    }
    let contactFormStyle = {
        maxWidth: 450,
        margin: 'auto',
        padding: '0 3%',
    }
    let contactFormBtn = {
        margin: 'auto',
        display: 'block',
        padding: '5px 25px',
        outline: 'none',
        background: 'rgba(0, 187, 16, .5)',
        border: '1pt solid white',
        color: 'white',
        fontSize: '1.2em',
        borderRadius: '25px',
    }
    let contactFormBtn2 = {
        margin: 'auto',
        display: 'block',
        padding: '5px 25px',
        outline: 'none',
        background: 'lightgray',
        border: '1pt solid white',
        color: 'white',
        fontSize: '1.2em',
        borderRadius: '25px',
    }

    return (
        <div className={"container"} id="contact-container" ref={hiddenRef}>
          <div className='contact-info'>
            <div className={`contact-info__link-${theme} home-contact-info__link1`}><a className={`contact-info-link`} target="_blank" href="mailto:wsright987@gmail.com" rel="noreferrer">wsright987@gmail.com</a></div>
            <div className={`contact-info__link-${theme} home-contact-info__link2`}><a className={`contact-info-link`} target="_blank" href="http://github.com/sleighs" rel="noreferrer">github.com/sleighs</a></div>
            <div className={`contact-info__link-${theme} home-contact-info__link3`}>tel <a className={`contact-info-link`} target="_blank" href="tel:609-472-2897" rel="noreferrer">+1 (609) 472-2897</a></div>
          </div>

          <div>
                <p style={{
                    textAlign: 'center',
                    display: 'none',
                }}>
                    Schedule an appointment 
                    <a style={{

                    }} href="https://calendly.com/sleighs" target="blank">here</a>
                </p>
            </div>

            {dataSent ?
            <div className="msg"style={{ textAlign: 'center' }}>
                <br/>
                <p style={{ fontSize: '1.2em' }}>Great news! Message sent successfully!</p>
                <button className="new-message-btn" 
                    style={contactFormBtn2} 
                    onClick={(e)=>{resetSend()}}>New Message</button>
            </div> :
            <form style={contactFormStyle} >
                <div style={formOptionStyle}>
                    <p>Or use this contact form below:</p>
                </div>
                <div className="form-group"> 
                    <label name="inputName">Your Name</label>
                    <input type="text" 
                        className="form-control" 
                        id="name-input" 
                        name="name" value={formData.name}  
                        onChange={(e)=>{handleChange(e)}}
                    />
                </div>
                <div className="form-group">
                    <label name="inputEmail">Your Email</label>
                    <input type="email" 
                        className="form-control" 
                        id="email-input" 
                        aria-describedby="emailHelp" 
                        value={formData.email} 
                        name="email"
                        onChange={(e)=>{handleChange(e)}}
                    />
                </div>
                <div className="form-group">
                    <label name="inputMessage" className="form-text">Your Message </label>
                    <textarea type="text" 
                        className="form-control" 
                        id="message-input" 
                        name="message" 
                        value={formData.message} 
                        onChange={(e)=>{handleChange(e)}}                    />
                </div>
                <input type="submit" className={"input for-control submit"} style={contactFormBtn} value="Send" onClick={(e)=>{handleSubmit(e)}}/>
                </form>}
        </div>
    )
}

export default Contact;
