import React, { Component, useState } from 'react';
import Cookies from 'universal-cookie'
import { 
  Header,
  Skills,
  Projects,
  Location,
  Contact,
  Footer
} from './Components';
import './App.css';

const cookiesDarkMode = new Cookies()

class Portfolio extends Component {
  constructor(props){
    super(props)
    this.state = {
      textColor: 'black',
      backgroundColor: 'white',
      darkMode: false,
    }

    this.toggleDarkMode = this.toggleDarkMode.bind(this);
  }

  toggleDarkMode(){
    if (!this.state.darkMode){
      this.setState({
        textColor: 'white',
        backgroundColor: 'black',
        darkMode: true,
      })
      cookiesDarkMode.set(
        'darkModeData', 
        true, 
        {path:'/', maxAge: 1200,}
      )
    } else {
      this.setState({
        textColor: 'black',
        backgroundColor: 'white',
        darkMode: false,
      })
      cookiesDarkMode.set(
        'darkModeData', 
        false, 
        {path:'/', maxAge: 1200,}
      )
    }

  }

  render() {
    let portfolioStyle = {
      fontSize: '1.2em',
      color: this.state.textColor,
      backgroundColor: this.state.backgroundColor
    }

    return (
      <div 
        id="portfolio" 
        className={"container-md"} 
        style={portfolioStyle}
      > 
        <DarkMode toggle={this.toggleDarkMode} modeState={this.state.darkMode}/>
        <Header />
        <Skills />
        <hr/>
        <Projects />
        <hr/>
        <Github />
        <hr/>
        <Location />
        <hr/>
        <Contact />
        <hr/>
        <Footer />
      </div>
    );
  }
}


class Github extends Component {
  render() {
    return(
      <div id="github-container" >
        <h3 className="title"><strong>DO I HAVE A GITHUB?</strong></h3>
        <p id="github-text"><a  href="http://www.github.com/sleighs" target="blank">Yes.</a></p>
      </div>
    )
  }

}

const DarkMode = (props) => {
  const { 
    modeState,
    toggle
  } = props

  const [darkModeState, getDarkModeState] = useState(modeState)

  return(
    <div id="dark-mode-container" 
      style={{
        display: 'none',
      }}>
      <button onClick={()=>{
        if (darkModeState === true){
          getDarkModeState(false)
          toggle()
        } else {
          toggle()
          getDarkModeState(true)
        }

        console.log('darkMode ', modeState )
      }}>
        {modeState === true ? 'Dark On' : 'Light On'}
      </button>
    </div>
  )
}

export default Portfolio;
