import { Map, GoogleApiWrapper, InfoWindow, Marker } from 'google-maps-react';
import '../App.css';

const mapStyle = {
  width: '450px',
  height: '300px',
  position: 'relative',
  display: 'block',
  margin: 'auto'
  ,float: 'left'

};

export class MapContainer extends Component {
    constructor(props){
        super(props);
        this.state ={
          showingInfoWindow: false,
          activeMarker: {},          
          selectedPlace: {}          
