import React, { useContext, useEffect, useRef, useState } from 'react';
import { DataContext } from '../../Context/DataContext';
import { ThemeContext } from '../../Context/ThemeContext';
import githubPic from '../../Resources/Images/portfolio-github.png';
import emailPic from '../../Resources/Images/envelope-solid.png';
import mobilePic from '../../Resources/Images/mobile-icon.png';
import mobile2Pic from '../../Resources/Images/phone.svg';
import mobile3Pic from '../../Resources/Images/phone-fill.svg';

import { db } from '../../firebase';
import './style.css'

const Contact = (props) => {
    const [formData, setFormData] = useState({
        name: '',
        email: '',
        message: '',
    })
    const [dataSent, setDataSent] = useState(false)

    const { setPageLocation } = useContext(DataContext)
    const { theme } = useContext(ThemeContext)

    const hiddenRef = useRef();

    useEffect(() => {
        window.addEventListener('scroll', scrollHandler);
        return () => window.removeEventListener('scroll', scrollHandler);
    }, []);

    const scrollHandler = () => {
        if(window.pageYOffset + window.innerHeight >= hiddenRef.current.offsetTop + 125) {
            setPageLocation('contact')
        }
    }

    function makeid(length) {
        var result = "";
        var characters =
          "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789";
        var charactersLength = 10;
        for (var i = 0; i < length; i++) {
          result += characters.charAt(Math.floor(Math.random() * charactersLength));
        }
        return result;
    }

    const handleChange = e => {
        setFormData(prevData => {
            return {
                ...prevData,
                [e.target.name]: e.target.value
            }
        })
    }

    function handleSubmit(event) {
        event.preventDafault()

        // Make a unique name for the message
        var d = new Date();
        var msgName = String(formData.name + '-' + d.getTime());

        // Get date of message
        var date = String( d.getMonth() + 1) + '-' + String( d.getDate()) + '-' + String(d.getFullYear())

        // Get time of message
        var minutes = ()=>{
            var a;
            if (d.getMinutes() < 10) {
                a = String('0' + d.getUTCMinutes());
            } else {
                a = String(d.getUTCMinutes());
            }

            return a;
        }

        var time = String(d.getHours()) + ':' + minutes() + '.' + String(d.getSeconds());

        // Make new id
        var id = makeid(10);

        // Add message to database
        db.collection("messages").doc(msgName).set({
            name: formData.name,
            email: formData.email,
            message: formData.message,
            date: date,
            time: time,
            timezone: d.getTimezoneOffset(),
            id: id
        })
        .then(() => {
            console.log("Great news! Message sent successfully!");
            /*console.log({
                name: formData.name,
                email: formData.email,
                message: formData.message,
                date: date,
                time: time,
                timezone: d.getTimezoneOffset(),
                id: id
            })*/
        })
        .catch((error) => {
            console.error("Krikey! Error sending message: ", error);
        });

        // For component to show message was sent
        if (!dataSent){
            setDataSent(true)
        } 
    }

    function resetSend() {
        setDataSent(false)
    }


    let contactInfoStyle = {
        textAlign: 'center',
        marginTop: 15,
        width: '100%',
    }
    let contactInfoStyle2 = {
        textAlign: 'center',
        fontSize: '.8em',
        color: 'rgb(1,1,1,.25)',
    }
    let formOptionStyle = {
        textAlign: 'center'
    }

    let contactFormBtn = {
        margin: 'auto',
        display: 'block',
        padding: '5px 25px',
        outline: 'none',
        background: 'rgba(0, 187, 16, .5)',
        border: '1pt solid white',
        color: 'white',
        fontSize: '1.2em',
        borderRadius: '25px',
    }
    let contactFormBtn2 = {
        margin: 'auto',
        display: 'block',
        padding: '5px 25px',
        outline: 'none',
        background: 'lightgray',
        border: '1pt solid white',
        color: 'white',
        fontSize: '1.2em',
        borderRadius: '25px',
    }

    return (
        <div className={"container"} id="contact-container" ref={hiddenRef}>
          <div className='contact__info'>
            <div className={`home-contact-info__link-${theme} home-contact-info__link1`}>
                <img className="" alt="" src={emailPic}
                    style={{
                        width: 20,
                        marginRight: 8,
                        marginBottom: 4,
                    }}/>
                <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-envelope-fill" viewBox="0 0 16 16" 
                    style={{
                        width: 20,
                        marginRight: 8,
                        marginBottom: 4,
                    }}>
                    <path d="M.05 3.555A2 2 0 0 1 2 2h12a2 2 0 0 1 1.95 1.555L8 8.414.05 3.555ZM0 4.697v7.104l5.803-3.558L0 4.697ZM6.761 8.83l-6.57 4.027A2 2 0 0 0 2 14h12a2 2 0 0 0 1.808-1.144l-6.57-4.027L8 9.586l-1.239-.757Zm3.436-.586L16 11.801V4.697l-5.803 3.546Z"/>
                </svg>
                <a className={`contact-info-link`} target="_blank" href="mailto:wsright987@gmail.com" rel="noreferrer">wsright987@gmail.com</a>
            </div>
            <div className={`home-contact-info__link-${theme} home-contact-info__link2`}>
                <img className="" alt="" src={githubPic}
                    style={{
                        width: 20,
                        marginRight: 8,
                        marginBottom: 4,
                    }}/>
                <a className={`contact-info-link`} target="_blank" href="http://github.com/sleighs" rel="noreferrer">github.com/sleighs</a>
            </div>
            <div className={`home-contact-info__link-${theme} home-contact-info__link3`}>

                <img className="" alt="" src={mobile3Pic}
                    style={{
                        width: 20,
                        marginRight: 8,
                        marginBottom: 4,
                        display: 'none',
                    }}/>
                <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-phone-fill" viewBox="0 0 16 16"
                    style={{
                        width: 20,
                        marginRight: 8,
                        marginBottom: 4,
                    }}>
                    <path d="M3 2a2 2 0 0 1 2-2h6a2 2 0 0 1 2 2v12a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2V2zm6 11a1 1 0 1 0-2 0 1 1 0 0 0 2 0z"/>
                </svg>

                <a className={`contact-info-link`} target="_blank" href="tel:609-472-2897" rel="noreferrer">+1 (609) 472-2897</a>
            </div>
          </div>

            <div>
                <p style={{
                    textAlign: 'center',
                    display: 'none',
                }}>
                    Schedule an appointment 
                    <a style={{

                    }} href="https://calendly.com/sleighs" target="blank">here</a>
                </p>
            </div>

            {dataSent 
            ? <div className="msg"style={{ textAlign: 'center' }}>
                <br/>
                <p style={{ fontSize: '1.2em' }}>Great news! Message sent successfully!</p>
                <button className="new-message-btn" 
                    style={contactFormBtn2} 
                    onClick={(e)=>{resetSend()}}>New Message</button>
            </div> 
            : <form className='contact-form' >
                <div style={/*formOptionStyle, */{display: 'none',}} >
                    <p>Or use this contact form below:</p>
                </div>
                <div style={{display: 'none',}} className="form-group contact-form__input-container"> 
                    <label name="inputName" className="form-text">Name</label>
                    <input type="text" 
                        className="form-control" 
                        id="name-input" 

                        name="name" value={formData.name}  
                        onChange={(e)=>{handleChange(e)}}
                    />
                </div>
                <div className="form-group contact-form__input-container">
                    <label name="inputEmail" className="form-text">Email</label>
                    <input type="email" 
                        className="form-control" 
                        id="email-input" 
                        placeholder='your@email.com'
                        aria-describedby="emailHelp" 
                        value={formData.email} 
                        name="email"
                        onChange={(e)=>{handleChange(e)}}
                    />
                </div>
                <div className="form-group contact-form__input-container">
                    <label name="inputMessage" className="form-text">Message</label>
                    <textarea type="text" 
                        className="form-control" 
                        id="message-input" 
                        placeholder='Message...'
                        name="message" 
                        value={formData.message} 
                        onChange={(e)=>{handleChange(e)}}                    />
                </div>
                <input type="submit" className={"input for-control submit"} style={contactFormBtn} value="Send" onClick={(e)=>{handleSubmit(e)}}/>
                </form>}
        </div>
    )
}

export default Contact;
