import React, { useEffect, useState } from 'react';
import '../../App.css';
import './style.css'

const Skills = (props) => {
  const [frontEndSkills, setFrontEndSkills]	= useState([
	'JavaScript', 
	'React',
	'Redux',
	'HTML5',
	'CSS3',
	'Sass',
	'Bootstrap',
  ])
  const [backEndSkills, setBackEndSkills] = useState([
	'MongoDB',
	'Node',
	'Python',
	'Firebase'
  ])
  const [otherSkills, setOtherSkills] = useState([
	'Git',
	'Adobe Xd',
	'Photoshop',
	'Linux',
	'Jest'
  ])

  const loadSkills = () => {
	console.log('skills loaded')

	var frontEndEle = document.getElementsByClassName('skills__front-end');
	var backEndEle = document.getElementsByClassName('skills__back-end');
	var otherEle = document.getElementsByClassName('skills__other');

	frontEndEle[0].innerHTML = '';
	backEndEle[0].innerHTML = '';
	otherEle[0].innerHTML = '';

	frontEndSkills.forEach((item, index)=>{
		var newLogoEle = document.createElement('div');
		var newImgEle = document.createElement('img');
		var newTextEle = document.createElement('span');

		newLogoEle.classList.add('skills__logo-container');
		newImgEle.classList.add('skills__logo-img');
		newTextEle.classList.add('skills__logo-text');

		newTextEle.innerText = item;
		newLogoEle.appendChild(newImgEle);
		newLogoEle.appendChild(newTextEle);

		frontEndEle[0].appendChild(newLogoEle);
	});
	backEndSkills.forEach((item, index)=>{
		var newLogoEle = document.createElement('div');
		var newImgEle = document.createElement('img');
		var newTextEle = document.createElement('span');

		newLogoEle.classList.add('skills__logo-container');
		newImgEle.classList.add('skills__logo-img');
		newTextEle.classList.add('skills__logo-text');

		newTextEle.innerText = item;
		newLogoEle.appendChild(newImgEle);
		newLogoEle.appendChild(newTextEle);

		backEndEle[0].appendChild(newLogoEle);
	});
	otherSkills.forEach((item, index)=>{
		return (<SkillBox name={item}/>)
	});
  }

  useEffect(()=>{
	//load skills
	loadSkills()
  },[])

  return (
	<div className="container container-md" id="skills-container">
	  <div className="skills__list" id="skills-list1">
		<h3>Front-end</h3>
		<div className="skills__front-end"></div>
	  </div>
	  <div className="skills__list" id="skills-list2">
		<h3>Back-end</h3>
		<div className="skills__back-end"></div>
	  </div>
	  <div className="skills__list" id="skills-list3">
		<h3>Other</h3>
		<div className="skills__other">{otherSkills.forEach((item, index)=>{
		return (<SkillBox name={item}/>)
	})}</div>
	  </div>
	</div>
  )
}


const SkillBox = (props) => {
	return (
		<div className="skills__logo-container">
			<img className="skills__logo-img" src='' alt='' />
			<span className="skills__logo-text">{props.name}</span>
		</div> 
	)
}

export default Skills;
